import * as L from 'leaflet';
import {inject} from 'aurelia-framework';
import {ReportCard} from 'utility/report-card';
import {EventAggregator} from 'aurelia-event-aggregator';

//start-aurelia-decorators
@inject(EventAggregator, ReportCard)
//end-aurelia-decorators
export class Location {
  constructor(EventAggregator, ReportCard) {
    this.ea = EventAggregator;
    this.reportcard = ReportCard;
    this.config = ReportCard.config;
  }

  drawGpsMarkers(center, accuracy, map) {
    L.circle(center, {
      weight: 0,
      fillColor: '#31aade',
      fillOpacity: 0.15,
      radius: accuracy / 2
    }).addTo(map);
    L.circleMarker(center, {
      color: 'white',
      weight: 1,
      fillColor: '#31aade',
      fillOpacity: 1,
      radius: 8
    }).addTo(map);
  }

  attached() {
    var self = this;

    //Add leaflet map
    self.map = L.map('mapWrapper', {
      attributionControl: false,
      center: self.config.map.start_city_center,
      zoom: 15
    });
    L.tileLayer(self.config.tile_layer, {
      detectRetina: true,
      ext: 'png'
    }).addTo(self.map);

    //Add custom leaflet control, to navigate back to browser located user location
    L.Control.GeoLocate = L.Control.extend({
      onAdd: function (map) {
        var container = L.DomUtil.create('div', 'leaflet-bar leaflet-control leaflet-control-custom');
        container.innerHTML = '<i class="icon-geolocate"></i>';
        container.style.fontSize = '21px';
        container.style.textAlign = 'center';
        container.style.lineHeight = '30px';
        container.style.color = 'black';
        container.style.backgroundColor = 'white';
        container.style.width = '30px';
        container.style.height = '30px';
        container.onclick = function() {
          if (self.reportcard.location.gpsLocation) {
            self.map.flyTo(self.reportcard.location.gpsLocation, 16);
          }
        };
        return container;
      }
    });
    L.control.geoLocate = function(opts) {
      return new L.Control.GeoLocate(opts);
    };

    //If previous inputs available, setView to user selected location
    if (self.reportcard.location.markerLocation) {
      self.map.setView(self.reportcard.location.markerLocation, 15);
      //If previous geolocation inputs available, add circle markers at gps location
      if (self.reportcard.location.gpsLocation) {
        L.control.geoLocate({position: 'bottomright'}).addTo(self.map);
        self.drawGpsMarkers(self.reportcard.location.gpsLocation, self.reportcard.location.accuracy, self.map);
      }
    } else if (!!navigator.geolocation) {
      //If previous inputs unavailable, i.e. at session start; try geolocation if supported by browser
      self.map.locate({
        setView: true
      });
      self.map.on('locationfound', (e) => {
        L.control.geoLocate({position: 'bottomright'}).addTo(self.map);
        self.drawGpsMarkers(e.latlng, e.accuracy, self.map);
        self.reportcard.location = {markerLocation: e.latlng, gpsLocation: e.latlng, accuracy: e.accuracy};
      });
      //If geolocation unavailable, go to default city center;
      self.map.on('locationerror', () => {
        self.reportcard.location.markerLocation = self.map.getCenter();
        self.ea.publish('geolocate', 'error');
      });
    } else {
      //Go to default city center if geolocation not supported by browser
      self.reportcard.location.markerLocation = self.map.getCenter();
    }

    //Get map center (corresponding to overlaid marker image) if user pans map
    self.map.on('moveend', () => {
      if (self.map) {
        self.reportcard.location.markerLocation = self.map.getCenter();
      }
    });
  }
}

import {inject} from 'aurelia-framework';
import $ from 'jquery';
import {ReportCard} from 'utility/report-card';
import {ImageUtility} from 'utility/image-utility';
import {EventAggregator} from 'aurelia-event-aggregator';
import {SignedRequest} from 'services/signed-request';

//start-aurelia-decorators
@inject(ReportCard, EventAggregator, ImageUtility, SignedRequest)
//end-aurelia-decorators
export class Photo {
  constructor(ReportCard, EventAggregator, ImageUtility, SignedRequest) {
    this.reportcard = ReportCard;
    this.img_utility = ImageUtility;
    this.ea = EventAggregator;
    this.signed_request = SignedRequest;
    if (this.reportcard.photo.file) {
      this.haveImg = true;
    }
    this.enableUpload = true;
  }

  uploadSupported() {
    var nua = navigator.userAgent.toLowerCase();
    var version;
    if ((nua.indexOf('android') >= 0) && (nua.indexOf('chrome') === -1)) { //android device, no chrome (add opera? etc.)
      var rest = nua.substring(nua.indexOf('android') + 8, nua.length);
      version = rest.substring(0, 3); //2-digit android version
      return (parseFloat(version) >= 4.4);
    } else {
      return true;
    }
  }

  attached() {
    if (this.uploadSupported()) {
      this.cntxt = this.preview.getContext('2d');
      $('#previewWrapper').addClass('enabled');
    } else {
      this.ea.publish('upload', 'error');
      this.enableUpload = false;
    }
    if (this.haveImg) {
      this.img_utility.drawImage(this.reportcard.photo.rotation, this.preview, 'canvas', this.reportcard.photo.file[0])
      .then(() => {
        $('#rotateButton').prop("disabled", false);
        $('#deleteButton').prop("disabled", false);
      });
    }
  }

  sendClick() {
    $('#ghostButton').trigger('click');
    this.notify = false;
  }

  sizeCheck() {
    if (this.reportcard.photo.file[0]) {
      if (this.reportcard.photo.file[0].size < 4404019) {
        this.img_utility.drawImage(0, this.preview, 'canvas', this.reportcard.photo.file[0])
        .then(() => {
          $('#rotateButton').prop("disabled", false);
          $('#deleteButton').prop("disabled", false);

          // get signedURL & store in reportcard
          this.signed_request.getSignedURL(this.reportcard.photo.file[0].type)
          .then(signedURL => {
            this.reportcard.photo.signedURL = signedURL;
          }).catch(error => {
            this.reportcard.photo.signedURL = 'url_error';
          });
        });
      } else {
        this.ea.publish('size', 'error');
        this.reportcard.photo.file = null;
      }
    }
  }

  rotatePhoto() {
    this.reportcard.photo.rotation += 90;
    this.img_utility.drawImage(this.reportcard.photo.rotation, this.preview, 'canvas', this.reportcard.photo.file[0])
    .then(() => {
      $('#rotateButton').prop("disabled", false);
      $('#deleteButton').prop("disabled", false);
    });
  }

  deletePhoto() {
    this.cntxt.translate(-this.preview.width / 2, -this.preview.height / 2);
    this.cntxt.clearRect(0, 0, this.preview.width, this.preview.height);

    this.reportcard.photo = {file: null, rotation: 0, signedURL: null};

    $('#rotateButton').prop("disabled", true);
    $('#deleteButton').prop("disabled", true);
  }
}

