//start-aurelia-decorators
@noView
@inject(ReportCard)
//end-aurelia-decorators
export class ReportSubmission {
  constructor(ReportCard) {
    this.reportcard = ReportCard;
    this.data_server = ReportCard.config.data_server;
  }

  putReport(report, id, router, hasPhoto, photoUploaded) {
    var self = this;

    // Define route settings pointers
    var error_settings, thanks_settings;
    for (let route of router.routes) {
      if (route.name === 'error') {
        error_settings = route.settings;
      }
      if (route.name === 'thanks') {
        thanks_settings = route.settings;
      }
    }

    // Construct http client
    let client_report = new HttpClient()
    .configure(x => {
      x.withBaseUrl(self.data_server + 'cards/');
    });

    // PUT reportcard data
    client_report.put(id, report)
    .then(put_success => {
      if (hasPhoto && photoUploaded) {
        // If photo uploaded successfully, patch image_url
        client_report.patch(id, {
          // TODO: match server patch handler
          image_url: id
        }).then(patch_success => {
          // Proceed to thanks page
          thanks_settings.code = 'pass';
          router.navigate('thanks');
        }).catch(patch_error => {
          // Proceed to thanks page with image upload error notification
          thanks_settings.code = 'fail';
          router.navigate('thanks');
        });
      } else if (hasPhoto && !photoUploaded) {
        // Proceed to thanks page with image upload error notification
        thanks_settings.code = 'fail';
        router.navigate('thanks');
      } else {
        // Proceed to thanks page
        thanks_settings.code = 'pass';
        router.navigate('thanks');
      }
    }).catch(put_error => {
      // Navigate to error page if PUT reportcard data fails
      error_settings.code = put_error.statusCode;
      error_settings.msg = put_error.statusText;
      router.navigate('error');
    });
  }

  submitReport(report, id, router) {
    var self = this;
    var photo = self.reportcard.photo.file[0];
    var signedURL = self.reportcard.photo.signedURL;

    if (photo && signedURL) {
      let client_photo = new HttpClient();

      if (signedURL === 'url_error') {
        // PUT report & notify user about upload error
        this.putReport(report, id, router, true, false);
      } else {
        // PUT photo in S3 bucket using signedURL
        client_photo.put(signedURL, photo)
        .then(success => {
          // PUT report & patch image_url
          this.putReport(report, id, router, true, true);
        }).catch(error => {
          // PUT report & notify user about upload error
          this.putReport(report, id, router, true, false);
        });
      }
    } else {
      // PUT report & proceed to thanks
      this.putReport(report, id, router, false, false);
    }
  }
}

import {noView, inject} from 'aurelia-framework';
import {HttpClient} from 'aurelia-http-client';
import {ReportCard} from 'utility/report-card';

//start-aurelia-decorators
@noView
@inject(ReportCard)
//end-aurelia-decorators
export class SignedRequest {
  constructor(ReportCard) {
    this.id = ReportCard.id;
    this.data_server = ReportCard.config.data_server;
  }

  getSignedURL(type) {
    var self = this;

    return new Promise(function(resolve, reject) {
      let client = new HttpClient()
      .configure(x => {
        x.withBaseUrl(self.data_server + 'cards/' + self.id);
        x.withHeader('Content-Type', type);
      });

      client.get('/images')
      .then(response => {
        let msg = JSON.parse(response.response);
        let signedURL = msg.signedRequest;
        resolve(signedURL);
      }).catch(error => {
        reject(error);
      });
    });
  }
}

import {noView} from 'aurelia-framework';

//start-aurelia-decorators
@noView
//end-aurelia-decorators
export class ImageUtility {
  drawImage(degrees, wrapper, wrapper_id, file) {
    return new Promise(function(resolve, reject) {
      var cntxt = wrapper.getContext('2d');
      wrapper.width = $('#' + wrapper_id).width();
      wrapper.height = $('#' + wrapper_id).height();
      let reader = new FileReader();
      reader.onload = (e) => {
        let reviewImg = new Image();
        reviewImg.onload = () => {
          let imgW;
          let imgH;
          let trlX = -wrapper.width/2;
          let trlY = -wrapper.height/2;
          if (reviewImg.width >= reviewImg.height) {
            imgH = wrapper.height;
            imgW = Math.round((reviewImg.width * imgH) / reviewImg.height);
            trlX = trlX + Math.round((wrapper.width - imgW) / 2);
          } else {
            imgW = wrapper.width;
            imgH = Math.round((reviewImg.height * imgW) / reviewImg.width);
            trlY = trlY + Math.round((wrapper.height - imgH) / 2);
          }
          cntxt.translate(wrapper.width / 2, wrapper.height / 2);
          cntxt.rotate(degrees * Math.PI / 180);
          cntxt.drawImage(reviewImg, trlX, trlY, imgW, imgH);
        };
        reviewImg.src = e.target.result;
      };
      reader.readAsDataURL(file);
      resolve(null);
    });
  }
}

import {Config} from 'resources/config';
import {noView, inject} from 'aurelia-framework';
import {LocaleEn} from 'resources/locales/en';
import {LocaleLocal} from 'resources/locales/local_lang';

//start-aurelia-decorators
@noView
@inject(LocaleEn, LocaleLocal, Config)
//end-aurelia-decorators
export class ReportCard {
  static metadata() {
    return Metadata.singleton(true);
  } //true indicates to register in the root container

  // Support language changing
  changeLanguage() {
    this.locale = this.lang_obj[this.selLanguage].translation_strings;
  }

  constructor(LocaleEn, LocaleLocal, Config) {
    var self = this;
    self.config = Config;
    self.selLanguage = Config.default_language;
    self.languages = Config.supported_languages;
    self.lang_obj = {en: LocaleEn};
    self.lang_obj[self.languages[1]] = LocaleLocal;
    self.locale = {};
    self.changeLanguage(self.selLanguage);

    self.disaster_type = null;
    self.location = {markerLocation: null, gpsLocation: null, accuracy: null, supported: false};
    self.depth = null; //TODO: make this object similar to DB structure, i.e. tags: {flood_depth: 50, report_type: 'treeclearing'.... etc}
    self.reportType = null;
    self.photo = {file: null, rotation: 0, signedURL: null};
    self.description = {value: null};
    self.network = null;
    self.errors = {code: null, text: null};
  }
}

import {noView, inject} from 'aurelia-framework';
import {Config} from 'resources/config';

//start-aurelia-decorators
@noView
@inject(Config)
//end-aurelia-decorators
export class Utility {
  constructor(Config) {
    this.config = Config;
    this.region_bounds = {};
    for (let city in Config.map.instance_regions) {
      this.region_bounds[city] = Config.map.instance_regions[city].bounds;
    }
  }

  setCardData(router) {
    var count;
    for (let i in router.routes) {
      if (router.routes[i].name === router.currentInstruction.fragment) {
        count = i;
      }
    }
    this.total_cards = router.routes.length - 2;
    this.card_count = parseInt(count);
  }

  resizeCardHt(factor) {
    var glitchHeight = 106;
    $('.card_content_wrapper').css({
      'height': $(document).height() - ($('.card_title_wrapper').height() + $('.card_navigation_wrapper').height() + (factor * glitchHeight)) + 'px'
    });
    $('.card_navigation_wrapper').css({
      'bottom': (factor * glitchHeight) + 'px'
    });
  }

  checkBrowserThenResize() {
    //Fit 'n' tab buttons on-the-fly, n = (all child-routes) - ([0] redirect route) - ('terms' route)
    $('.tabButtons').width((100 / this.total_cards) + '%');

    var nua = navigator.userAgent.toLowerCase();
    //______________is Mobile______________________an iPhone_________________browser not safari (in-app)___________app is twitter________________app is facebook______________not facebook messenger_________
    if ((/Mobi/.test(navigator.userAgent)) && nua.indexOf('iphone') > -1 && nua.indexOf('safari') === -1 && (nua.indexOf('twitter') > -1 || (nua.indexOf('fban') > -1 && nua.indexOf('messenger') === -1))) {
      this.resizeCardHt(1);
    } else {
      //Execute resize on initial page load
      this.resizeCardHt(0);
    }
  }

  disableNext(router, reportcard) {
    if (router.currentInstruction.fragment === 'location') {
      //disable next if gps error and location not selected
      return !reportcard.location.markerLocation;
    } else if (router.currentInstruction.fragment === 'prep') {
      //disable next if prep type not selected
      return !reportcard.reportType;
    } else if (router.currentInstruction.fragment === 'depth') {
      //disable next if depth slider not dragged
      return !this.sliderDragged;
    } else {
      //disable next for review, terms cards
      return this.card_count >= this.total_cards;
    }
  }

  isLocationSupported(reportcard) {
    var self = this;
    var l = reportcard.location.markerLocation;
    reportcard.location.supported = false;

    for (let city in self.region_bounds) {
      if (l.lat > self.region_bounds[city].sw[0] && l.lng > self.region_bounds[city].sw[1] && l.lat < self.region_bounds[city].ne[0] && l.lng < self.region_bounds[city].ne[1]) {
        reportcard.location.supported = true;
        break;
      }
    }
    return reportcard.location.supported;
  }

  navigate(order, router, reportcard) {
    var self = this;
    if (order === 'next') {
      if (router.currentInstruction.fragment === 'location') {
        if (self.isLocationSupported(reportcard) || self.location_check) {
          self.card_count += 1;
          router.navigate(router.routes[self.card_count].route);
          self.closeNotification();
        }
        if (!self.location_check && !self.isLocationSupported(reportcard)) {
          self.showNotification('warning', 'location_2', 'location_2', false);
          self.location_check = true; // execute showNotification once
        }
      } else if (router.currentInstruction.fragment !== 'location' && self.card_count < self.total_cards) {
        self.card_count += 1;
        router.navigate(router.routes[self.card_count].route);
        self.closeNotification();
      }
    } else if (order === 'prev') {
      if (self.card_count > 1) {
        if (router.currentInstruction.fragment !== 'terms') {
          self.card_count -= 1;
        }
        router.navigate(router.routes[self.card_count].route);
      }
    }
  }

  showNotification(type, header, message, bespoke) {
    var self = this;
    self.notify_type = type;
    self.notify_header = header;
    self.notify_message = message;
    if (bespoke) {
      self.notify_custom = true;
    } else {
      self.notify_custom = false;
    }
    if ($('#notifyWrapper').hasClass('active')) {
      $('#notifyWrapper').finish();
    }
    $('#notifyWrapper').slideDown(300, () => {
      $('#notifyWrapper').addClass('active');
    }).delay(5000).slideUp(300, () => {
      $('#notifyWrapper').removeClass('active');
    });
  }

  closeNotification() {
    if ($('#notifyWrapper').hasClass('active')) {
      $('#notifyWrapper').dequeue();
    }
  }
}
